scripts.setup:react:
yarn add react react-dom && yarn add -D @vitejs/plugin-react-refresh && printf 'import reactRefresh from "@vitejs/plugin-react-refresh";'$'
'"$(cat vite.config.js)" > vite.config.js && if [ ! -f src/App.jsx ]; then printf 'import React from "react";

const App = () => <p>Hello, React.</p>;

export default App;

' > src/App.jsx; fi && mv src/index.js src/index.jsx && printf 'import React from "react";
import ReactDOM from "react-dom";
import App from "./App";

ReactDOM.render(<App />, document.getElementById("root"));
'$'
'"$(cat src/index.jsx)" > src/index.jsx && sed -i '' 's/plugins: \[/plugins: \[reactRefresh(),/g' vite.config.js
